import React, { useEffect, useCallback } from 'react';

const Modal = ({ isOpen, onClose, images, currentIndex, onNext, onPrev, showNav = true }) => {
  const handleKeyDown = useCallback((e) => {
    if (!isOpen) return;
    
    if (e.key === 'Escape') {
      onClose();
    } else if (e.key === 'ArrowLeft' && onPrev && showNav) {
      onPrev();
    } else if (e.key === 'ArrowRight' && onNext && showNav) {
      onNext();
    }
  }, [isOpen, onClose, onNext, onPrev, showNav]);

  useEffect(() => {
    document.addEventListener('keydown', handleKeyDown);
    
    if (isOpen) {
      document.body.style.overflow = 'hidden';
    }
    
    return () => {
      document.removeEventListener('keydown', handleKeyDown);
      document.body.style.overflow = '';
    };
  }, [isOpen, handleKeyDown]);

  if (!isOpen || !images || images.length === 0) return null;

  const currentImage = images[currentIndex];

  return (
    <div className={`modal ${isOpen ? 'show' : ''}`} onClick={onClose}>
      <div className="modal__container" onClick={(e) => e.stopPropagation()}>
        <button className="modal__close" onClick={onClose}>&times;</button>
        
        <div className="modal__image-wrapper">
          <img 
            src={currentImage.src} 
            alt={currentImage.caption || 'Gallery image'} 
            className="modal__image"
          />
        </div>
        
        {showNav && images.length > 1 && (
          <div className="modal__nav">
            <button 
              className="modal__nav-button modal__nav-button--prev"
              onClick={onPrev}
              style={{ display: currentIndex === 0 ? 'none' : 'block' }}
            >
              &lt;
            </button>
            <button 
              className="modal__nav-button modal__nav-button--next"
              onClick={onNext}
              style={{ display: currentIndex === images.length - 1 ? 'none' : 'block' }}
            >
              &gt;
            </button>
          </div>
        )}
        
        {currentImage.caption && (
          <div className="modal__caption">{currentImage.caption}</div>
        )}
      </div>
    </div>
  );
};

export default Modal;
