import React, { useState, useEffect } from 'react';

const Hero = () => {
  const titles = ['Web Developer', 'IT Student', 'Problem Solver'];
  const [currentTitleIndex, setCurrentTitleIndex] = useState(0);
  const [isAnimating, setIsAnimating] = useState(false);

  useEffect(() => {
    const interval = setInterval(() => {
      setIsAnimating(true);
      setTimeout(() => {
        setCurrentTitleIndex(prev => (prev + 1) % titles.length);
        setIsAnimating(false);
      }, 500);
    }, 4000);

    return () => clearInterval(interval);
  }, [titles.length]);

  const scrollToSection = (sectionId) => {
    const element = document.getElementById(sectionId);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <section id="hero" className="fade-in">
      <div className="container">
        <div className="hero-content">
          <div className="hero-text">
            <h1 className="hero-name">Carl Luis</h1>
            <h2 className={`hero-title ${isAnimating ? 'fade-out' : 'fade-in'}`}>
              {titles[currentTitleIndex]}
            </h2>
            <h3 className="hero-subtitle">IT STUDENT AT CVSU SILANG</h3>
            <p className="hero-intro">
              An aspiring Web Developer with a passion for creating user-friendly and 
              visually appealing websites. Currently a third-year IT student from 
              General Trias, Cavite, combining technical expertise with creative problem-solving.
            </p>
            <div className="hero-cta">
              <button 
                className="cta-button"
                onClick={() => scrollToSection('projects')}
              >
                View My Work
              </button>
              <button 
                className="cta-button secondary"
                onClick={() => scrollToSection('about')}
              >
                Learn More
              </button>
            </div>
          </div>
          <div className="hero-image-wrapper">
            <img src="/owes.jpg" alt="Carl Luis C. Dimapilis" className="profile-image" />
          </div>
        </div>
      </div>
    </section>
  );
};

export default Hero;
