import React, { useState } from 'react';
import Modal from './Modal';

const Quizzes = () => {
  const [modalOpen, setModalOpen] = useState(false);
  const [currentImage, setCurrentImage] = useState(null);

  const preTestQuizzes = [
    {
      id: 1,
      title: 'JavaScript Fundamentals',
      grade: 'A+',
      score: '95/100',
      date: 'January 2024',
      image: '/img/brawls.jpg'
    },
    {
      id: 2,
      title: 'Web Development Basics',
      grade: 'A',
      score: '90/100',
      date: 'December 2023',
      image: '/img/steam.jpg'
    }
  ];

  const openModal = (quiz) => {
    setCurrentImage({
      src: quiz.image,
      caption: `${quiz.title} - ${quiz.grade} - ${quiz.score}`
    });
    setModalOpen(true);
  };

  const closeModal = () => {
    setModalOpen(false);
    setCurrentImage(null);
  };

  return (
    <section id="quizzes" className="quizzes-section">
      <div className="container">
        <div className="quizzes-hero-content">
          <h2 className="section-title">My Quizzes</h2>
          <p className="quizzes-subtitle">A record of my quiz results and achievements.</p>
        </div>

        <div className="quiz-section-wrapper">
          <h3 className="quiz-section-title">Pre-Test Results</h3>
          <div className="quiz-grid">
            {preTestQuizzes.map((quiz) => (
              <article key={quiz.id} className="quiz-item">
                <div className="quiz-header">
                  <h4>{quiz.title}</h4>
                  <span className="quiz-grade">Grade: {quiz.grade}</span>
                </div>
                <div className="quiz-content">
                  <div 
                    className="quiz-paper-preview"
                    onClick={() => openModal(quiz)}
                  >
                    <img 
                      src={quiz.image} 
                      alt={`${quiz.title} Quiz Paper`} 
                      className="quiz-paper-thumb"
                    />
                    <div className="preview-overlay">
                      <span>View</span>
                    </div>
                  </div>
                  <div className="quiz-meta">
                    <span className="quiz-date">Completed: {quiz.date}</span>
                    <span className="quiz-score">Score: {quiz.score}</span>
                  </div>
                </div>
              </article>
            ))}
          </div>
        </div>
      </div>

      {modalOpen && currentImage && (
        <Modal 
          isOpen={modalOpen}
          onClose={closeModal}
          images={[currentImage]}
          currentIndex={0}
          showNav={false}
        />
      )}
    </section>
  );
};

export default Quizzes;
