import React, { useState, useEffect } from 'react';

const Header = ({ theme, toggleTheme }) => {
  const [menuOpen, setMenuOpen] = useState(false);
  const [isVisible, setIsVisible] = useState(true);
  const [lastScrollY, setLastScrollY] = useState(0);

  const navItems = [
    { id: 'hero', label: 'Home' },
    { id: 'about', label: 'About' },
    { id: 'skills', label: 'Skills' },
    { id: 'projects', label: 'Projects' },
    { id: 'quizzes', label: 'Quizzes' },
    { id: 'certifications', label: 'Certifications' }
  ];

  useEffect(() => {
    const handleScroll = () => {
      const currentScrollY = window.scrollY;
      
      if (currentScrollY > lastScrollY && currentScrollY > 100) {
        setIsVisible(false);
      } else {
        setIsVisible(true);
      }
      
      setLastScrollY(currentScrollY);
    };

    window.addEventListener('scroll', handleScroll, { passive: true });
    return () => window.removeEventListener('scroll', handleScroll);
  }, [lastScrollY]);

  const scrollToSection = (sectionId) => {
    const element = document.getElementById(sectionId);
    if (element) {
      const headerOffset = 80;
      const elementPosition = element.getBoundingClientRect().top;
      const offsetPosition = elementPosition + window.pageYOffset - headerOffset;

      window.scrollTo({
        top: offsetPosition,
        behavior: 'smooth'
      });
    }
    setMenuOpen(false);
  };

  return (
    <header 
      className={`site-header ${!isVisible ? 'header-hidden' : ''}`} 
      role="banner"
    >
      <nav className="container" role="navigation" aria-label="Main navigation">
        <a href="#hero" className="logo" aria-label="Home" onClick={(e) => {
          e.preventDefault();
          scrollToSection('hero');
        }}>OWES</a>
        
        <button 
          className={`menu-toggle ${menuOpen ? 'active' : ''}`}
          aria-label="Toggle menu" 
          aria-expanded={menuOpen}
          onClick={() => setMenuOpen(!menuOpen)}
        >
          <span></span>
          <span></span>
          <span></span>
        </button>

        <ul className={`main-nav ${menuOpen ? 'active' : ''}`} role="menu">
          {navItems.map(item => (
            <li key={item.id} role="none">
              <a 
                href={`#${item.id}`}
                role="menuitem"
                onClick={(e) => {
                  e.preventDefault();
                  scrollToSection(item.id);
                }}
              >
                {item.label}
              </a>
            </li>
          ))}
          <li role="none">
            <button 
              className="theme-toggle"
              onClick={toggleTheme}
              aria-label={`Switch to ${theme === 'light' ? 'dark' : 'light'} mode`}
            >
              <i className={`fas ${theme === 'light' ? 'fa-moon' : 'fa-sun'}`}></i>
            </button>
          </li>
        </ul>
      </nav>
    </header>
  );
};

export default Header;
