import React, { useState, useEffect, useRef } from 'react';

const Skills = () => {
  const [animated, setAnimated] = useState(false);
  const sectionRef = useRef(null);

  const devSkills = [
    { name: 'JavaScript', icon: '/img/javascript.jpg', percent: 75 },
    { name: 'C++', icon: '/img/c++.jpg', percent: 85 },
    { name: 'CSharp', icon: '/img/Csharp.jpeg', percent: 88 },
    { name: 'SQLite', icon: '/img/sqlite.jpeg', percent: 88 },
    { name: 'Xamarin', icon: '/img/Xamarin.png', percent: 90 },
    { name: 'VS Code', icon: '/img/vs.jpg', percent: 90 }
  ];

  const gamingSkills = [
    { name: 'Valorant', icon: '/img/valo.jpg', percent: 95 },
    { name: 'CoC', icon: '/img/clash.jpg', percent: 88 },
    { name: 'Roblox', icon: '/img/rob.jpg', percent: 88 },
    { name: 'BrawlStars', icon: '/img/brawls.jpg', percent: 88 },
    { name: 'CS:GO', icon: '/img/csgo.jpg', percent: 88 },
    { name: 'LoL', icon: '/img/lol.jpg', percent: 92 }
  ];

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting && !animated) {
            setAnimated(true);
          }
        });
      },
      { threshold: 0.3 }
    );

    if (sectionRef.current) {
      observer.observe(sectionRef.current);
    }

    return () => observer.disconnect();
  }, [animated]);

  const SkillItem = ({ skill }) => (
    <div className="skill-item">
      <img src={skill.icon} alt={skill.name} className="skill-icon" />
      <span>{skill.name}</span>
      <div className="skill-progress">
        <div 
          className="progress-bar" 
          style={{ width: animated ? `${skill.percent}%` : '0%' }}
          data-percent={skill.percent}
        >
          <span className="percent">{skill.percent}%</span>
        </div>
      </div>
    </div>
  );

  return (
    <section id="skills" className="skills-section" ref={sectionRef}>
      <div className="container">
        <h2 className="section-title">Skills & Technologies</h2>
        <div className="skills-grid">
          <article className="skill-category">
            <h3>Development</h3>
            <div className="skill-items">
              {devSkills.map((skill, index) => (
                <SkillItem key={index} skill={skill} />
              ))}
            </div>
          </article>

          <article className="skill-category">
            <h3>Gaming & Entertainment</h3>
            <div className="skill-items">
              {gamingSkills.map((skill, index) => (
                <SkillItem key={index} skill={skill} />
              ))}
            </div>
          </article>
        </div>
      </div>
    </section>
  );
};

export default Skills;
