import React, { useState } from 'react';

const Certifications = () => {
  const [activePopup, setActivePopup] = useState(null);

  const certifications = [
    {
      id: 'networking',
      title: 'Networking Basics',
      description: 'Cisco-verified completion of Networking Basics course, demonstrating fundamental networking concepts and protocols.',
      image: '/img/CertNetworking.png',
      credentialUrl: 'https://www.credly.com/earner/earned/badge/4aa04497-0bfc-4373-9e29-03f7dbb6ecfc'
    },
    {
      id: 'intro',
      title: 'Introduction To Cisco',
      description: 'Certified proficiency in creating digital models of IP Networks and IoT Systems using Cisco Packet Tracer.',
      image: '/img/CertIntro.png',
      credentialUrl: 'https://www.credly.com/earner/earned/badge/530197dc-068c-41d1-9d30-ecb172fca2b3'
    }
  ];

  return (
    <section id="certifications" className="certifications-section">
      <div className="container">
        <h2 className="section-title">Certifications</h2>
        <p className="section-subtitle">Professional achievements and credentials</p>
        <div className="certifications-grid">
          {certifications.map((cert) => (
            <div key={cert.id} className="certification-item fade-in">
              <div className="badge-item">
                <img 
                  src={cert.image} 
                  alt={`${cert.title} Certification`} 
                  className="cert-image"
                />
                <div className="certification-content">
                  <h3>{cert.title}</h3>
                  <p>{cert.description}</p>
                  <button 
                    className="view-credential"
                    onClick={() => setActivePopup(cert.id)}
                  >
                    View Credential
                    <i className="fas fa-arrow-right"></i>
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Certification Popups */}
      {certifications.map((cert) => (
        <div 
          key={cert.id}
          className={`certification-popup ${activePopup === cert.id ? 'active' : ''}`}
          onClick={(e) => {
            if (e.target.className.includes('certification-popup')) {
              setActivePopup(null);
            }
          }}
        >
          <div className="popup-content">
            <span 
              className="popup-close"
              onClick={() => setActivePopup(null)}
            >
              &times;
            </span>
            <img 
              src={cert.image} 
              alt={`${cert.title} Certification`} 
              className="popup-image"
            />
            <div className="popup-details">
              <h3>{cert.title}</h3>
              <p>{cert.description}</p>
              <a 
                href={cert.credentialUrl} 
                className="view-credential" 
                target="_blank"
                rel="noopener noreferrer"
              >
                View Credential
                <i className="fas fa-arrow-right"></i>
              </a>
            </div>
          </div>
        </div>
      ))}
    </section>
  );
};

export default Certifications;
