import React from 'react';

const Footer = () => {
  const socialLinks = [
    { href: 'mailto:sc.carlluis.dimapilis@cvsu.edu.ph', icon: 'fa fa-envelope', label: 'Email' },
    { href: 'https://github.com/Uwisss', icon: 'fab fa-github', label: 'GitHub' },
    { href: 'https://www.linkedin.com/in/dimapilis-carl-luis-c-66b63631b/', icon: 'fab fa-linkedin', label: 'LinkedIn' }
  ];

  return (
    <footer className="site-footer">
      <div className="container">
        <div className="footer-content">
          <div className="footer-info">
            <h3 className="footer-title">Carl Luis C. Dimapilis</h3>
            <p className="footer-tagline">Web Developer & IT Student</p>
          </div>
          <div className="footer-social">
            <h3 className="footer-title">Connect With Me</h3>
            <ul className="social-links">
              {socialLinks.map((link, index) => (
                <li key={index}>
                  <a 
                    href={link.href} 
                    target="_blank" 
                    rel="noopener noreferrer"
                    aria-label={link.label} 
                    className="social-link"
                  >
                    <i className={link.icon}></i>
                    <span>{link.label}</span>
                  </a>
                </li>
              ))}
            </ul>
          </div>
        </div>
        <div className="footer-bottom">
          <p className="copyright">Â© 2026 Carl Luis C. Dimapilis. All rights reserved.</p>
        </div>
      </div>
    </footer>
  );
};

export default Footer;
