import React, { useState, useEffect, useRef } from 'react';

const Skills = () => {
  const [animated, setAnimated] = useState(false);
  const sectionRef = useRef(null);

  const frontendSkills = [
    { name: 'HTML5', icon: 'ðŸŒ', percent: 90 },
    { name: 'CSS3', icon: 'ðŸŽ¨', percent: 85 },
    { name: 'JavaScript', icon: 'âš¡', percent: 75 },
    { name: 'React', icon: 'âš›ï¸', percent: 70 },
  ];

  const backendSkills = [
    { name: 'C#', icon: 'ðŸ’»', percent: 88 },
    { name: 'C++', icon: 'ðŸ”§', percent: 85 },
    { name: 'SQLite', icon: 'ðŸ—„ï¸', percent: 88 },
    { name: 'Xamarin', icon: 'ðŸ“±', percent: 90 },
  ];

  const toolsSkills = [
    { name: 'VS Code', icon: 'ðŸ“', percent: 95 },
    { name: 'Git', icon: 'ðŸ”€', percent: 80 },
    { name: 'Figma', icon: 'ðŸŽ¯', percent: 70 },
    { name: 'Packet Tracer', icon: 'ðŸŒ', percent: 85 },
  ];

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting && !animated) {
            setAnimated(true);
          }
        });
      },
      { threshold: 0.2 }
    );

    if (sectionRef.current) {
      observer.observe(sectionRef.current);
    }

    return () => observer.disconnect();
  }, [animated]);

  const SkillItem = ({ skill, delay }) => (
    <div className="skill-item" style={{ animationDelay: `${delay}ms` }}>
      <div className="skill-header">
        <span className="skill-emoji">{skill.icon}</span>
        <span className="skill-name">{skill.name}</span>
        <span className="skill-percent">{skill.percent}%</span>
      </div>
      <div className="skill-progress">
        <div 
          className="progress-bar" 
          style={{ 
            width: animated ? `${skill.percent}%` : '0%',
            transitionDelay: `${delay}ms`
          }}
        />
      </div>
    </div>
  );

  const SkillCategory = ({ title, skills, icon }) => (
    <article className="skill-category">
      <div className="category-header">
        <span className="category-icon">{icon}</span>
        <h3>{title}</h3>
      </div>
      <div className="skill-items">
        {skills.map((skill, index) => (
          <SkillItem key={index} skill={skill} delay={index * 100} />
        ))}
      </div>
    </article>
  );

  return (
    <section id="skills" className="skills-section" ref={sectionRef}>
      <div className="container">
        <h2 className="section-title">Skills & Expertise</h2>
        <p className="section-subtitle">Technologies I work with</p>
        <div className="skills-grid">
          <SkillCategory title="Frontend" skills={frontendSkills} icon="ðŸŽ¨" />
          <SkillCategory title="Backend" skills={backendSkills} icon="âš™ï¸" />
          <SkillCategory title="Tools" skills={toolsSkills} icon="ðŸ› ï¸" />
        </div>
      </div>
    </section>
  );
};

export default Skills;
