import React from 'react';

const Footer = () => {
  const socialLinks = [
    { href: 'mailto:carlluis57@gmail.com', icon: 'fas fa-envelope', label: 'Email' },
    { href: 'https://github.com/Uwisss', icon: 'fab fa-github', label: 'GitHub' },
    { href: 'https://www.linkedin.com/in/dimapilis-carl-luis-c-66b63631b/', icon: 'fab fa-linkedin', label: 'LinkedIn' },
    { href: 'https://www.facebook.com/carlluis.dimapilis', icon: 'fab fa-facebook', label: 'Facebook' }
  ];

  const scrollToTop = () => {
    window.scrollTo({ top: 0, behavior: 'smooth' });
  };

  return (
    <footer className="site-footer">
      <div className="container">
        <div className="footer-main">
          <div className="footer-brand">
            <h3 className="footer-logo">OWES</h3>
            <p>Creating digital experiences with passion and precision.</p>
          </div>
          
          <div className="footer-links">
            <h4>Quick Links</h4>
            <ul>
              <li><a href="#hero">Home</a></li>
              <li><a href="#about">About</a></li>
              <li><a href="#projects">Projects</a></li>
              <li><a href="#certifications">Certifications</a></li>
            </ul>
          </div>

          <div className="footer-social">
            <h4>Connect</h4>
            <div className="social-icons">
              {socialLinks.map((link, index) => (
                <a 
                  key={index}
                  href={link.href} 
                  target="_blank" 
                  rel="noopener noreferrer"
                  aria-label={link.label}
                  className="social-icon"
                >
                  <i className={link.icon}></i>
                </a>
              ))}
            </div>
          </div>
        </div>

        <div className="footer-bottom">
          <p className="copyright">
            Â© {new Date().getFullYear()} Carl Luis C. Dimapilis. All rights reserved.
          </p>
          <button className="back-to-top" onClick={scrollToTop} aria-label="Back to top">
            <i className="fas fa-arrow-up"></i>
          </button>
        </div>
      </div>
    </footer>
  );
};

export default Footer;
